import React, { useState } from 'react';
import { X, Wand2, MessageSquare, Sparkles, RotateCcw, Copy, Check } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

const AIAssistPanel = ({ isOpen, onClose, onInsert, storyId, actions }) => {
  // Single view with two generators
  // Removed mode - now unified generation
  const [loading, setLoading] = useState(false);
  const [suggestion, setSuggestion] = useState('');
  const [contextUsed, setContextUsed] = useState(null);
  const [copied, setCopied] = useState(false);
  const [showDialogue, setShowDialogue] = useState(false);

  // Form state
  const [seedIdea, setSeedIdea] = useState('');
  const [includePrevious, setIncludePrevious] = useState(true);
  const [visualType, setVisualType] = useState('2D');
  const [entities, setEntities] = useState([]);
  const [selectedEntity, setSelectedEntity] = useState('');
  const [entityDescription, setEntityDescription] = useState('');
  const [mood, setMood] = useState('natural');
  const [characters, setCharacters] = useState('');
  const [tone, setTone] = useState('natural');
  const [beat, setBeat] = useState('');
  const [length, setLength] = useState('short');
  const [entityDetails, setEntityDetails] = useState({}); // Maps entity name to description

  // @mention functionality
  const [showMentions, setShowMentions] = useState(false);
  const [mentionFilter, setMentionFilter] = useState('');
  const [cursorPosition, setCursorPosition] = useState(0);

  const presetEntities = ['person', 'dog', 'cat', 'animal', 'vehicle', 'bird', 'child', 'robot', 'horse', 'spaceship', 'building', 'tree'];

  const addEntity = () => {
    const entity = selectedEntity.trim();
    if (!entity) return;

    setEntities(prev => {
      const newEntities = Array.from(new Set([...prev, entity]));
      return newEntities;
    });

    if (entityDescription?.trim()) {
      setEntityDetails(prev => ({
        ...prev,
        [entity]: entityDescription.trim()
      }));
    }

    setSelectedEntity('');
    setEntityDescription('');
  };

  const removeEntity = (value) => {
    setEntities(prev => prev.filter(e => e !== value));
    setEntityDetails(prev => {
      const next = { ...prev };
      delete next[value];
      return next;
    });
  };

  const handleEntityKeyDown = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addEntity();
    }
  };


  const handleGenerate = async () => {
    if (!seedIdea.trim()) return;

    setLoading(true);
    try {
      const payload = {
        seedIdea: seedIdea.trim(),
        includePrevious: includePrevious && storyId, // Only include context if we have a story
        visualType,
        entities,
        entityDetails,
        mood,
        wantsDialogue: showDialogue,
      };

      if (showDialogue) {
        payload.characters = characters.split(',').map(c => c.trim()).filter(Boolean);
        payload.tone = tone;
        payload.beat = beat.trim();
        payload.length = length;
      }

      // For first scene creation (no storyId), use a dummy story ID to trigger generation
      const targetStoryId = storyId || 'new-story';
      const result = await actions.assistPrompt(targetStoryId, payload);
      setSuggestion(result.prompt || '');
      setContextUsed(result.contextUsed);
    } catch (error) {
      console.error('Failed to get AI assistance:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = async () => {
    if (!suggestion) return;
    try {
      await navigator.clipboard.writeText(suggestion);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (error) {
      console.error('Failed to copy:', error);
    }
  };

  const handleInsert = () => {
    if (suggestion) {
      onInsert(suggestion);
      onClose();
    }
  };

  // Unified generation - no separate functions needed

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center"
          onClick={onClose}
        >
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.9, opacity: 0 }}
            className="glass-effect rounded-xl w-full max-w-2xl max-h-[90vh] overflow-hidden"
            onClick={(e) => e.stopPropagation()}
          >
            {/* Header */}
            <div className="p-6 border-b border-white/10">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 rounded-xl bg-gradient-to-r from-cosmic-accent/20 to-cosmic-magenta/20 flex items-center justify-center">
                    <Sparkles className="w-5 h-5 text-cosmic-accent" />
                  </div>
                  <div>
                    <h3 className="text-xl font-bold text-white">AI Scene Assistant</h3>
                    <p className="text-white/60 text-sm">Get help with prompts and dialogue</p>
                  </div>
                </div>
                <button
                  onClick={onClose}
                  className="glass-effect p-2 rounded-lg text-white hover:bg-white/10 transition-colors"
                >
                  <X className="w-5 h-5" />
                </button>
              </div>



              {/* Controls */}
              <div className="mt-6 grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-white text-sm font-medium mb-2">Type</label>
                  <select
                    value={visualType}
                    onChange={(e) => setVisualType(e.target.value)}
                    className="w-full glass-effect rounded-lg p-3 text-white bg-cosmic-dark/50 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                  >
                    <option value="2D">2D Animation</option>
                    <option value="3D">3D Animation</option>
                    <option value="live_action">Real Life</option>
                    <option value="stop_motion">Stop Motion</option>
                  </select>
                </div>
                <div>
                  <label className="block text-white text-sm font-medium mb-2">Mood</label>
                  <select
                    value={mood}
                    onChange={(e) => setMood(e.target.value)}
                    className="w-full glass-effect rounded-lg p-3 text-white bg-cosmic-dark/50 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                  >
                    <option value="natural">Natural</option>
                    <option value="witty">Witty</option>
                    <option value="tense">Tense</option>
                    <option value="heartfelt">Heartfelt</option>
                    <option value="comedic">Comedic</option>
                    <option value="dramatic">Dramatic</option>
                    <option value="melancholic">Melancholic</option>
                    <option value="triumphant">Triumphant</option>
                  </select>
                </div>
                <div className="md:col-span-2">
                  <label className="block text-white text-sm font-medium mb-2">Entities</label>
                  <div className="space-y-2">
                    <select
                      value={selectedEntity}
                      onChange={(e) => setSelectedEntity(e.target.value)}
                      className="w-full glass-effect rounded-lg p-3 text-white bg-cosmic-dark/50 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                    >
                      <option value="">Select an entity...</option>
                      {presetEntities.map((entity) => (
                        <option key={entity} value={entity}>
                          {entity}
                        </option>
                      ))}
                    </select>
                    <input
                      type="text"
                      value={entityDescription}
                      onChange={(e) => setEntityDescription(e.target.value)}
                      onKeyDown={handleEntityKeyDown}
                      placeholder="Description (optional): red sports car, fluffy golden retriever, tall muscular person..."
                      className="w-full glass-effect rounded-lg p-3 text-white placeholder-white/40 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                    />
                    <button
                      type="button"
                      onClick={addEntity}
                      disabled={!selectedEntity}
                      className="w-full glass-effect px-4 py-3 rounded-lg text-white hover:bg-white/10 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      Add Entity
                    </button>
                  </div>
                  {entities.length > 0 && (
                    <div className="flex flex-wrap gap-2 mt-2">
                      {entities.map((e) => (
                        <span key={e} className="px-3 py-1 rounded-full text-xs bg-white/10 text-white border border-white/20 flex items-center gap-2">
                          <span>
                            {e}
                            {entityDetails[e] && <span className="text-white/70"> - {entityDetails[e]}</span>}
                          </span>
                          <button type="button" onClick={() => removeEntity(e)} className="hover:text-cosmic-accent">Ã—</button>
                        </span>
                      ))}
                    </div>
                  )}
                </div>

                <div className="md:col-span-2 flex items-center justify-between">
                  <div className="flex items-center space-x-2 text-white/80">
                    <Wand2 className="w-4 h-4" />
                    <span className="font-medium">Scene</span>
                  </div>
                  <button
                    onClick={() => setShowDialogue(!showDialogue)}
                    className="glass-effect px-4 py-2 rounded-lg text-white hover:bg-white/10 transition-colors flex items-center space-x-2"
                  >
                    <MessageSquare className="w-4 h-4" />
                    <span>{showDialogue ? 'Hide Dialogue' : 'Add Dialogue'}</span>
                  </button>
                </div>
              </div>
            </div>

            {/* Content */}
            <div className="p-6 space-y-6 max-h-[60vh] overflow-y-auto">
              {/* Context Toggle */}
              {storyId && (
                <div className="flex items-center justify-between">
                  <label className="flex items-center space-x-3 cursor-pointer">
                    <input
                      type="checkbox"
                      checked={includePrevious}
                      onChange={(e) => setIncludePrevious(e.target.checked)}
                      className="w-4 h-4 text-cosmic-accent bg-white/10 border-white/20 rounded focus:ring-cosmic-accent"
                    />
                    <span className="text-white text-sm">Use previous scenes for context</span>
                  </label>
                </div>
              )}

              {!storyId && (
                <div className="glass-effect rounded-lg p-3 border border-cosmic-accent/20">
                  <p className="text-white/70 text-sm">
                    âœ¨ This is your first scene! No previous context available yet.
                  </p>
                </div>
              )}

              <div className="space-y-4">
                <div>
                  <label className="block text-white text-sm font-medium mb-2">
                    Scene Idea
                  </label>
                  <textarea
                    value={seedIdea}
                    onChange={(e) => setSeedIdea(e.target.value)}
                    placeholder="Describe what should happen in this scene..."
                    className="w-full glass-effect rounded-lg p-3 text-white placeholder-white/40 resize-none h-24 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                  />
                </div>
              </div>

              {showDialogue && (
                <div className="space-y-4">
                  <div>
                    <label className="block text-white text-sm font-medium mb-2">
                      Characters (comma-separated)
                    </label>
                    <input
                      type="text"
                      value={characters}
                      onChange={(e) => setCharacters(e.target.value)}
                      placeholder="Alex, Sam, Morgan"
                      className="w-full glass-effect rounded-lg p-3 text-white placeholder-white/40 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-white text-sm font-medium mb-2">
                        Tone
                      </label>
                      <select
                        value={tone}
                        onChange={(e) => setTone(e.target.value)}
                        className="w-full glass-effect rounded-lg p-3 text-white bg-cosmic-dark/50 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                      >
                        <option value="natural">Natural</option>
                        <option value="witty">Witty</option>
                        <option value="tense">Tense</option>
                        <option value="heartfelt">Heartfelt</option>
                        <option value="comedic">Comedic</option>
                        <option value="dramatic">Dramatic</option>
                      </select>
                    </div>

                    <div>
                      <label className="block text-white text-sm font-medium mb-2">
                        Length
                      </label>
                      <select
                        value={length}
                        onChange={(e) => setLength(e.target.value)}
                        className="w-full glass-effect rounded-lg p-3 text-white bg-cosmic-dark/50 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                      >
                        <option value="very_short">Very Short</option>
                        <option value="short">Short</option>
                        <option value="medium">Medium</option>
                      </select>
                    </div>
                  </div>

                  <div>
                    <label className="block text-white text-sm font-medium mb-2">
                      What should this dialogue achieve?
                    </label>
                    <input
                      type="text"
                      value={beat}
                      onChange={(e) => setBeat(e.target.value)}
                      placeholder="Reveal a secret, build tension, make plans..."
                      className="w-full glass-effect rounded-lg p-3 text-white placeholder-white/40 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                    />
                  </div>

                </div>
              )}

              {/* Unified Generate Button */}
              <button
                onClick={handleGenerate}
                disabled={loading || !seedIdea.trim()}
                className="w-full glass-effect rounded-lg p-3 text-white hover:bg-white/10 transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center space-x-2"
              >
                {loading ? (
                  <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                ) : (
                  <>
                    <Sparkles className="w-5 h-5" />
                    <span>Generate {showDialogue ? 'Scene with Dialogue' : 'Scene'}</span>
                  </>
                )}
              </button>

              {/* Results */}
              {suggestion && (
                <div className="space-y-4">
                  <div className="glass-effect rounded-lg p-4">
                    <div className="flex items-center justify-between mb-3">
                      <h4 className="text-white font-medium">AI Suggestion</h4>
                      <div className="flex items-center space-x-2">
                        <button
                          onClick={handleCopy}
                          className="glass-effect p-2 rounded-lg text-white hover:bg-white/10 transition-colors"
                        >
                          {copied ? <Check className="w-4 h-4" /> : <Copy className="w-4 h-4" />}
                        </button>
                        <button
                          onClick={handleGenerate}
                          disabled={loading}
                          className="glass-effect p-2 rounded-lg text-white hover:bg-white/10 transition-colors"
                        >
                          <RotateCcw className="w-4 h-4" />
                        </button>
                      </div>
                    </div>
                    <p className="text-white/90 whitespace-pre-wrap">{suggestion}</p>

                    {contextUsed && (
                      <div className="mt-3 pt-3 border-t border-white/10">
                        <p className="text-white/50 text-xs">
                          {contextUsed.previousPrompts?.length > 0 && `Used context from ${contextUsed.previousPrompts.length} previous scene(s)`}
                          {contextUsed.usedLastFrame && ' â€¢ Used last frame for visual continuity'}
                        </p>
                      </div>
                    )}
                  </div>

                  <button
                    onClick={handleInsert}
                    className="w-full bg-cosmic-primary hover:bg-cosmic-primary/80 text-white rounded-lg p-3 transition-colors font-medium"
                  >
                    Insert into Scene Description
                  </button>
                </div>
              )}
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default AIAssistPanel;
