import React, { useState, useEffect, useRef, useCallback } from 'react';
import { X, Wand2, MessageSquare, Sparkles, RotateCcw, Copy, Check } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

const AIAssistPanel = ({ isOpen, onClose, onInsert, storyId, actions }) => {
  // Single view with two generators
  // Removed mode - now unified generation
  const [loading, setLoading] = useState(false);
  const [suggestion, setSuggestion] = useState('');
  const [contextUsed, setContextUsed] = useState(null);
  const [copied, setCopied] = useState(false);
  const [showDialogue, setShowDialogue] = useState(false);

  // Form state
  const [seedIdea, setSeedIdea] = useState('');
  const [includePrevious, setIncludePrevious] = useState(true);
  const [visualType, setVisualType] = useState('2D');
  const [entities, setEntities] = useState([]);
  const [selectedEntity, setSelectedEntity] = useState('');
  const [entityDescription, setEntityDescription] = useState('');
  const [mood, setMood] = useState('natural');
  const [characters, setCharacters] = useState('');
  const [tone, setTone] = useState('natural');
  const [beat, setBeat] = useState('');
  const [length, setLength] = useState('short');
  const [entityDetails, setEntityDetails] = useState({}); // Maps entity name to description

  // @mention functionality
  const [showMentions, setShowMentions] = useState(false);
  const [mentionFilter, setMentionFilter] = useState('');
  const [cursorPosition, setCursorPosition] = useState(0);
  const [selectedMentionIndex, setSelectedMentionIndex] = useState(0);
  const textareaRef = useRef(null);

  // Entity library state
  const [libraryEntities, setLibraryEntities] = useState([]);
  const [showLibrary, setShowLibrary] = useState(false);
  const [entitySource, setEntitySource] = useState('preset'); // 'preset' or 'library' or 'new'

  const presetEntities = ['person', 'dog', 'cat', 'animal', 'vehicle', 'bird', 'child', 'robot', 'horse', 'spaceship', 'building', 'tree'];

  // Define loadEntityLibrary first so it can be used in useEffect hooks
  const loadEntityLibrary = useCallback(async () => {
    try {
      const entities = await actions.getEntityLibrary();
      setLibraryEntities(entities);
    } catch (error) {
      console.error('Failed to load entity library:', error);
    }
  }, [actions]);

  // Close mentions dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (textareaRef.current && !textareaRef.current.contains(event.target)) {
        setShowMentions(false);
      }
    };

    if (showMentions) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showMentions]);

  // Load entity library when component mounts or when library is shown
  useEffect(() => {
    if (showLibrary && libraryEntities.length === 0) {
      loadEntityLibrary();
    }
  }, [showLibrary, libraryEntities.length, loadEntityLibrary]);

  // Load entity library when component opens for @mentions
  useEffect(() => {
    if (isOpen && libraryEntities.length === 0) {
      loadEntityLibrary();
    }
  }, [isOpen, libraryEntities.length, loadEntityLibrary]);

  const addEntity = () => {
    const entity = selectedEntity.trim();
    if (!entity) return;

    setEntities(prev => {
      const newEntities = Array.from(new Set([...prev, entity]));
      return newEntities;
    });

    if (entityDescription?.trim()) {
      setEntityDetails(prev => ({
        ...prev,
        [entity]: entityDescription.trim()
      }));
    }

    setSelectedEntity('');
    setEntityDescription('');
  };

  const saveCurrentEntityToLibrary = async () => {
    if (!selectedEntity.trim()) return;

    try {
      await actions.saveEntityToLibrary({
        name: selectedEntity.trim(),
        description: entityDescription.trim()
      });

      // Reload library to show the new entity
      loadEntityLibrary();
    } catch (error) {
      console.error('Failed to save entity to library:', error);
    }
  };

  const addFromLibrary = (libraryEntity) => {
    const entityName = libraryEntity.name;

    setEntities(prev => {
      const newEntities = Array.from(new Set([...prev, entityName]));
      return newEntities;
    });

    if (libraryEntity.description?.trim()) {
      setEntityDetails(prev => ({
        ...prev,
        [entityName]: libraryEntity.description.trim()
      }));
    }
  };

  const deleteFromLibrary = async (entityId) => {
    try {
      const success = await actions.deleteEntityFromLibrary(entityId);
      if (success) {
        setLibraryEntities(prev => prev.filter(e => e.id !== entityId));
      }
    } catch (error) {
      console.error('Failed to delete entity from library:', error);
    }
  };

  const removeEntity = (value) => {
    setEntities(prev => prev.filter(e => e !== value));
    setEntityDetails(prev => {
      const next = { ...prev };
      delete next[value];
      return next;
    });
  };

  const handleEntityKeyDown = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addEntity();
    }
  };

  // @mention functionality for scene idea input
  const handleSceneIdeaChange = (e) => {
    const value = e.target.value;
    const position = e.target.selectionStart;

    setSeedIdea(value);
    setCursorPosition(position);

    // Check if @ was typed and show mentions
    const beforeCursor = value.substring(0, position);
    const lastAtIndex = beforeCursor.lastIndexOf('@');

    if (lastAtIndex !== -1) {
      const afterAt = beforeCursor.substring(lastAtIndex + 1);
      // Only show mentions if @ is at start of word (preceded by space or start of string)
      const charBeforeAt = lastAtIndex > 0 ? beforeCursor[lastAtIndex - 1] : ' ';
      if (charBeforeAt === ' ' || lastAtIndex === 0) {
        // Check if there's a space after the @ (if so, don't show mentions)
        if (!afterAt.includes(' ')) {
          setMentionFilter(afterAt.toLowerCase());
          setSelectedMentionIndex(0);
          setShowMentions(true);
          return;
        }
      }
    }

    setShowMentions(false);
  };

  const handleSceneIdeaKeyDown = (e) => {
    if (!showMentions) return;

    const filtered = filteredEntities;
    if (filtered.length === 0) return;

    if (e.key === 'ArrowDown') {
      e.preventDefault();
      setSelectedMentionIndex(prev => 
        prev < filtered.length - 1 ? prev + 1 : 0
      );
    } else if (e.key === 'ArrowUp') {
      e.preventDefault();
      setSelectedMentionIndex(prev => 
        prev > 0 ? prev - 1 : filtered.length - 1
      );
    } else if (e.key === 'Enter' || e.key === 'Tab') {
      e.preventDefault();
      insertMention(filtered[selectedMentionIndex]);
    } else if (e.key === 'Escape') {
      e.preventDefault();
      setShowMentions(false);
    }
  };

  const insertMention = (entity) => {
    const beforeCursor = seedIdea.substring(0, cursorPosition);
    const afterCursor = seedIdea.substring(cursorPosition);
    const lastAtIndex = beforeCursor.lastIndexOf('@');

    if (lastAtIndex !== -1) {
      const beforeAt = beforeCursor.substring(0, lastAtIndex);
      const newValue = beforeAt + '@' + entity + ' ' + afterCursor;
      setSeedIdea(newValue);
      setShowMentions(false);

      // If this entity is from library and not already in current scene, add its description
      const libraryEntity = libraryEntities.find(le => le.name === entity);
      if (libraryEntity && !entities.includes(entity) && libraryEntity.description) {
        setEntityDetails(prev => ({
          ...prev,
          [entity]: libraryEntity.description
        }));
      }
    }
  };

  // Helper function to highlight @mentions in text
  const highlightMentions = (text) => {
    if (!text) return [{ type: 'text', content: '', key: 'text-0' }];

    // Create a list of all known entities (current + library)
    const allKnownEntities = [
      ...entities,
      ...libraryEntities.map(le => le.name)
    ];

    // Sort by length (longest first) to handle overlapping names correctly
    const sortedEntities = [...new Set(allKnownEntities)].sort((a, b) => b.length - a.length);

    // Find all @mentions in the text, checking against known entities
    const parts = [];
    let lastIndex = 0;

    // Use a more comprehensive regex that handles spaces and special characters in entity names
    const mentionRegex = /@([a-zA-Z0-9\s]+?)(?=\s|$|[^\w\s])/g;
    let match;

    while ((match = mentionRegex.exec(text)) !== null) {
      const fullMention = match[0];
      const mentionText = match[1].trim();

      // Check if this mention matches any known entity
      const matchedEntity = sortedEntities.find(entity => 
        entity.toLowerCase() === mentionText.toLowerCase()
      );

      if (matchedEntity) {
        // Add text before the mention
        if (match.index > lastIndex) {
          parts.push({
            type: 'text',
            content: text.substring(lastIndex, match.index),
            key: `text-${lastIndex}-${match.index}`
          });
        }

        // Add the mention with styling
        parts.push({
          type: 'mention',
          content: fullMention,
          entity: matchedEntity,
          isKnown: true,
          key: `mention-${match.index}`
        });

        lastIndex = match.index + fullMention.length;
      } else {
        // If not a known entity, treat the @ as part of regular text
        // Reset lastIndex to continue from after the @
        if (match.index > lastIndex) {
          parts.push({
            type: 'text',
            content: text.substring(lastIndex, match.index + 1),
            key: `text-${lastIndex}-${match.index + 1}`
          });
          lastIndex = match.index + 1;
        }
      }
    }

    // Add remaining text
    if (lastIndex < text.length) {
      parts.push({
        type: 'text',
        content: text.substring(lastIndex),
        key: `text-${lastIndex}-end`
      });
    }

    // If no parts were created, return the original text
    return parts.length > 0 ? parts : [{ type: 'text', content: text, key: 'text-0' }];
  };

  const filteredEntities = [
    // Current scene entities
    ...entities.filter(entity => 
      entity.toLowerCase().includes(mentionFilter)
    ),
    // Library entities (that are not already in current scene)
    ...libraryEntities
      .filter(libraryEntity => 
        !entities.includes(libraryEntity.name) && 
        libraryEntity.name.toLowerCase().includes(mentionFilter)
      )
      .map(libraryEntity => libraryEntity.name)
  ];


  const handleGenerate = async () => {
    if (!seedIdea.trim()) return;

    // Normalize @mentions in the seed idea to plain names so the model
    // does not echo raw tokens like "@person" in the suggestion
    const normalizedSeedIdea = seedIdea.replace(/@([a-zA-Z0-9_\s-]+)/g, (_, name) => name.trim());

    setLoading(true);
    try {
      const payload = {
        seedIdea: normalizedSeedIdea.trim(),
        includePrevious: includePrevious && storyId, // Only include context if we have a story
        visualType,
        entities,
        entityDetails,
        mood,
        wantsDialogue: showDialogue,
      };

      if (showDialogue) {
        payload.characters = characters.split(',').map(c => c.trim()).filter(Boolean);
        payload.tone = tone;
        payload.beat = beat.trim();
        payload.length = length;
      }

      // For first scene creation (no storyId), use a dummy story ID to trigger generation
      const targetStoryId = storyId || 'new-story';
      const result = await actions.assistPrompt(targetStoryId, payload);
      setSuggestion(result.prompt || '');
      setContextUsed(result.contextUsed);
    } catch (error) {
      console.error('Failed to get AI assistance:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = async () => {
    if (!suggestion) return;
    try {
      await navigator.clipboard.writeText(suggestion);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (error) {
      console.error('Failed to copy:', error);
    }
  };

  const handleInsert = () => {
    if (suggestion) {
      onInsert(suggestion);
      onClose();
    }
  };

  // Unified generation - no separate functions needed

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center"
          onClick={onClose}
        >
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.9, opacity: 0 }}
            className="glass-effect rounded-xl w-full max-w-2xl max-h-[90vh] overflow-hidden"
            onClick={(e) => e.stopPropagation()}
          >
            {/* Header */}
            <div className="p-6 border-b border-white/10">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 rounded-xl bg-gradient-to-r from-cosmic-accent/20 to-cosmic-magenta/20 flex items-center justify-center">
                    <Sparkles className="w-5 h-5 text-cosmic-accent" />
                  </div>
                  <div>
                    <h3 className="text-xl font-bold text-white">AI Scene Assistant</h3>
                    <p className="text-white/60 text-sm">Get help with prompts and dialogue</p>
                  </div>
                </div>
                <button
                  onClick={onClose}
                  className="glass-effect p-2 rounded-lg text-white hover:bg-white/10 transition-colors"
                >
                  <X className="w-5 h-5" />
                </button>
              </div>



              {/* Controls */}
              <div className="mt-6 grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-white text-sm font-medium mb-2">Type</label>
                  <select
                    value={visualType}
                    onChange={(e) => setVisualType(e.target.value)}
                    className="w-full glass-effect rounded-lg p-3 text-white bg-cosmic-dark/50 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                  >
                    <option value="2D">2D Animation</option>
                    <option value="3D">3D Animation</option>
                    <option value="live_action">Real Life</option>
                    <option value="stop_motion">Stop Motion</option>
                  </select>
                </div>
                <div>
                  <label className="block text-white text-sm font-medium mb-2">Mood</label>
                  <select
                    value={mood}
                    onChange={(e) => setMood(e.target.value)}
                    className="w-full glass-effect rounded-lg p-3 text-white bg-cosmic-dark/50 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                  >
                    <option value="natural">Natural</option>
                    <option value="witty">Witty</option>
                    <option value="tense">Tense</option>
                    <option value="heartfelt">Heartfelt</option>
                    <option value="comedic">Comedic</option>
                    <option value="dramatic">Dramatic</option>
                    <option value="melancholic">Melancholic</option>
                    <option value="triumphant">Triumphant</option>
                  </select>
                </div>
                <div className="md:col-span-2">
                  <div className="flex items-center justify-between mb-2">
                    <label className="block text-white text-sm font-medium">Entities</label>
                    <div className="flex space-x-2">
                      <button
                        type="button"
                        onClick={() => setEntitySource('preset')}
                        className={`px-3 py-1 rounded-lg text-xs transition-colors ${
                          entitySource === 'preset' ? 'bg-cosmic-accent/30 text-white' : 'glass-effect text-white/70 hover:text-white'
                        }`}
                      >
                        Presets
                      </button>
                      <button
                        type="button"
                        onClick={() => {
                          setEntitySource('library');
                          setShowLibrary(true);
                        }}
                        className={`px-3 py-1 rounded-lg text-xs transition-colors ${
                          entitySource === 'library' ? 'bg-cosmic-accent/30 text-white' : 'glass-effect text-white/70 hover:text-white'
                        }`}
                      >
                        Library
                      </button>
                      <button
                        type="button"
                        onClick={() => setEntitySource('new')}
                        className={`px-3 py-1 rounded-lg text-xs transition-colors ${
                          entitySource === 'new' ? 'bg-cosmic-accent/30 text-white' : 'glass-effect text-white/70 hover:text-white'
                        }`}
                      >
                        Create New
                      </button>
                    </div>
                  </div>

                  {/* Preset Entity Selection */}
                  {entitySource === 'preset' && (
                    <div className="space-y-2">
                      <select
                        value={selectedEntity}
                        onChange={(e) => setSelectedEntity(e.target.value)}
                        className="w-full glass-effect rounded-lg p-3 text-white bg-cosmic-dark/50 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                      >
                        <option value="">Select an entity...</option>
                        {presetEntities.map((entity) => (
                          <option key={entity} value={entity}>
                            {entity}
                          </option>
                        ))}
                      </select>
                      <input
                        type="text"
                        value={entityDescription}
                        onChange={(e) => setEntityDescription(e.target.value)}
                        onKeyDown={handleEntityKeyDown}
                        placeholder="Description (optional): red sports car, fluffy golden retriever..."
                        className="w-full glass-effect rounded-lg p-3 text-white placeholder-white/40 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                      />
                      <div className="flex space-x-2">
                        <button
                          type="button"
                          onClick={addEntity}
                          disabled={!selectedEntity}
                          className="flex-1 glass-effect px-4 py-3 rounded-lg text-white hover:bg-white/10 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                        >
                          Add Entity
                        </button>
                        {selectedEntity && (
                          <button
                            type="button"
                            onClick={saveCurrentEntityToLibrary}
                            className="glass-effect px-4 py-3 rounded-lg text-white hover:bg-white/10 transition-colors"
                            title="Save to library"
                          >

                          </button>
                        )}
                      </div>
                    </div>
                  )}

                  {/* Library Entity Selection */}
                  {entitySource === 'library' && (
                    <div className="space-y-2">
                      {libraryEntities.length === 0 ? (
                        <div className="glass-effect rounded-lg p-4 text-center text-white/70">
                          <p className="text-sm">No entities saved to library yet.</p>
                          <p className="text-xs mt-1">Create entities from presets and save them to build your library.</p>
                        </div>
                      ) : (
                        <div className="max-h-40 overflow-y-auto space-y-2">
                          {libraryEntities.map((entity) => (
                            <div
                              key={entity.id}
                              className="glass-effect rounded-lg p-3 flex items-center justify-between group"
                            >
                              <div className="flex-1">
                                <h4 className="text-white font-medium text-sm">{entity.name}</h4>
                                {entity.description && (
                                  <p className="text-white/60 text-xs mt-1">{entity.description}</p>
                                )}
                              </div>
                              <div className="flex space-x-2 opacity-0 group-hover:opacity-100 transition-opacity">
                                <button
                                  type="button"
                                  onClick={() => addFromLibrary(entity)}
                                  className="text-cosmic-accent hover:text-cosmic-accent/80 text-sm font-medium"
                                >
                                  Add
                                </button>
                                <button
                                  type="button"
                                  onClick={() => deleteFromLibrary(entity.id)}
                                  className="text-red-400 hover:text-red-300 text-sm"
                                >
                                  Delete
                                </button>
                              </div>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  )}

                  {/* Create New Entity */}
                  {entitySource === 'new' && (
                    <div className="space-y-2">
                      <input
                        type="text"
                        value={selectedEntity}
                        onChange={(e) => setSelectedEntity(e.target.value)}
                        placeholder="Entity name (e.g., red dragon, spaceship, wizard)"
                        className="w-full glass-effect rounded-lg p-3 text-white placeholder-white/40 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                      />
                      <input
                        type="text"
                        value={entityDescription}
                        onChange={(e) => setEntityDescription(e.target.value)}
                        onKeyDown={handleEntityKeyDown}
                        placeholder="Description (optional): detailed appearance, characteristics..."
                        className="w-full glass-effect rounded-lg p-3 text-white placeholder-white/40 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                      />
                      <div className="flex space-x-2">
                        <button
                          type="button"
                          onClick={addEntity}
                          disabled={!selectedEntity}
                          className="flex-1 glass-effect px-4 py-3 rounded-lg text-white hover:bg-white/10 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                        >
                          Add Entity
                        </button>
                        {selectedEntity && (
                          <button
                            type="button"
                            onClick={saveCurrentEntityToLibrary}
                            className="glass-effect px-4 py-3 rounded-lg text-white hover:bg-white/10 transition-colors"
                            title="Save to library"
                          >

                          </button>
                        )}
                      </div>
                    </div>
                  )}

                  {/* Current Scene Entities */}
                  {entities.length > 0 && (
                    <div className="mt-4">
                      <h4 className="text-white text-sm font-medium mb-2">Current Scene Entities:</h4>
                      <div className="flex flex-wrap gap-2">
                        {entities.map((e) => (
                          <span key={e} className="px-3 py-1 rounded-full text-xs bg-white/10 text-white border border-white/20 flex items-center gap-2">
                            <span>
                              {e}
                              {entityDetails[e] && <span className="text-white/70"> - {entityDetails[e]}</span>}
                            </span>
                            <button type="button" onClick={() => removeEntity(e)} className="hover:text-cosmic-accent"></button>
                          </span>
                        ))}
                      </div>
                    </div>
                  )}
                </div>

                <div className="md:col-span-2 flex items-center justify-between">
                  <div className="flex items-center space-x-2 text-white/80">
                    <Wand2 className="w-4 h-4" />
                    <span className="font-medium">Scene</span>
                  </div>
                  <button
                    onClick={() => setShowDialogue(!showDialogue)}
                    className="glass-effect px-4 py-2 rounded-lg text-white hover:bg-white/10 transition-colors flex items-center space-x-2"
                  >
                    <MessageSquare className="w-4 h-4" />
                    <span>{showDialogue ? 'Hide Dialogue' : 'Add Dialogue'}</span>
                  </button>
                </div>
              </div>
            </div>

            {/* Content */}
            <div className="p-6 space-y-6 max-h-[60vh] overflow-y-auto">
              {/* Context Toggle */}
              {storyId && (
                <div className="flex items-center justify-between">
                  <label className="flex items-center space-x-3 cursor-pointer">
                    <input
                      type="checkbox"
                      checked={includePrevious}
                      onChange={(e) => setIncludePrevious(e.target.checked)}
                      className="w-4 h-4 text-cosmic-accent bg-white/10 border-white/20 rounded focus:ring-cosmic-accent"
                    />
                    <span className="text-white text-sm">Use previous scenes for context</span>
                  </label>
                </div>
              )}

              {!storyId && (
                <div className="glass-effect rounded-lg p-3 border border-cosmic-accent/20">
                  <p className="text-white/70 text-sm">
                     This is your first scene! No previous context available yet.
                  </p>
                </div>
              )}

              <div className="space-y-4">
                <div className="relative">
                  <label className="block text-white text-sm font-medium mb-2">
                    Scene Idea
                    {(entities.length > 0 || libraryEntities.length > 0) && (
                      <span className="text-white/50 text-xs ml-2">
                        (Type @ to mention entities)
                      </span>
                    )}
                  </label>

                  {/* Custom highlighted text input */}
                  <div className="relative w-full glass-effect rounded-lg overflow-hidden focus-within:ring-2 focus-within:ring-cosmic-accent focus-within:border-transparent">
                    {/* Highlighted overlay */}
                    <div 
                      className="absolute inset-0 p-3 text-white whitespace-pre-wrap pointer-events-none break-words font-mono text-sm leading-5 z-20"
                      style={{ 
                        fontFamily: 'inherit',
                        fontSize: 'inherit',
                        lineHeight: 'inherit',
                        letterSpacing: 'inherit'
                      }}
                    >
                      {seedIdea?.length === 0 ? (
                        <span className="text-white/40">Describe what should happen in this scene...</span>
                      ) : highlightMentions(seedIdea).map((part) => {
                        if (part.type === 'mention') {
                          // Get entity description for tooltip
                          const entityDescription = entityDetails[part.entity] || 
                            libraryEntities.find(le => le.name === part.entity)?.description;
                          const tooltipText = entityDescription 
                            ? `${part.entity}: ${entityDescription}` 
                            : `Entity: ${part.entity}`;

                          return (
                            <span 
                              key={part.key}
                              className={`${
                                part.isKnown 
                                  ? 'bg-cosmic-violet/30 text-cosmic-violet rounded px-1 border border-cosmic-violet/50 cursor-help' 
                                  : 'bg-orange-500/30 text-orange-300 rounded px-1 border border-orange-500/50'
                              }`}
                              title={tooltipText}
                            >
                              {part.content}
                            </span>
                          );
                        } else {
                          return (
                            <span key={part.key} className="text-transparent">
                              {part.content}
                            </span>
                          );
                        }
                      })}
                    </div>

                    {/* Actual textarea */}
                    <textarea
                      ref={textareaRef}
                      value={seedIdea}
                      onChange={handleSceneIdeaChange}
                      onKeyDown={handleSceneIdeaKeyDown}
                      placeholder="Describe what should happen in this scene..."
                      className="relative w-full h-24 p-3 bg-transparent text-transparent placeholder-white/40 resize-none border-none outline-none font-mono text-sm leading-5 z-10"
                      style={{ 
                        caretColor: 'white',
                        fontFamily: 'inherit',
                        fontSize: 'inherit',
                        lineHeight: 'inherit',
                        letterSpacing: 'inherit'
                      }}
                    />
                  </div>

                  {/* @mentions dropdown */}
                  {showMentions && filteredEntities.length > 0 && (
                    <div className="absolute z-50 mt-1 w-full max-h-40 overflow-y-auto glass-effect rounded-lg border border-white/20 bg-cosmic-dark/90">
                      {filteredEntities.map((entity, index) => {
                        // Get description from either current entities or library
                        const currentDescription = entityDetails[entity];
                        const libraryEntity = libraryEntities.find(le => le.name === entity);
                        const description = currentDescription || libraryEntity?.description;
                        const isFromLibrary = !entities.includes(entity) && libraryEntity;

                        return (
                          <button
                            key={entity}
                            onClick={() => insertMention(entity)}
                            className={`w-full text-left px-4 py-2 text-white transition-colors flex items-center justify-between ${
                              index === selectedMentionIndex ? 'bg-cosmic-accent/30' : 'hover:bg-white/10'
                            }`}
                          >
                            <div className="flex-1">
                              <span className="flex items-center">
                                {entity}
                                {isFromLibrary && (
                                  <span className="ml-2 text-xs bg-cosmic-accent/20 text-cosmic-accent px-1 rounded">
                                    library
                                  </span>
                                )}
                              </span>
                              {description && (
                                <div className="text-white/50 text-xs truncate mt-1">
                                  {description}
                                </div>
                              )}
                            </div>
                          </button>
                        );
                      })}
                    </div>
                  )}
                </div>
              </div>

              {showDialogue && (
                <div className="space-y-4">
                  <div>
                    <label className="block text-white text-sm font-medium mb-2">
                      Characters (comma-separated)
                    </label>
                    <input
                      type="text"
                      value={characters}
                      onChange={(e) => setCharacters(e.target.value)}
                      placeholder="Alex, Sam, Morgan"
                      className="w-full glass-effect rounded-lg p-3 text-white placeholder-white/40 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-white text-sm font-medium mb-2">
                        Tone
                      </label>
                      <select
                        value={tone}
                        onChange={(e) => setTone(e.target.value)}
                        className="w-full glass-effect rounded-lg p-3 text-white bg-cosmic-dark/50 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                      >
                        <option value="natural">Natural</option>
                        <option value="witty">Witty</option>
                        <option value="tense">Tense</option>
                        <option value="heartfelt">Heartfelt</option>
                        <option value="comedic">Comedic</option>
                        <option value="dramatic">Dramatic</option>
                      </select>
                    </div>

                    <div>
                      <label className="block text-white text-sm font-medium mb-2">
                        Length
                      </label>
                      <select
                        value={length}
                        onChange={(e) => setLength(e.target.value)}
                        className="w-full glass-effect rounded-lg p-3 text-white bg-cosmic-dark/50 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                      >
                        <option value="very_short">Very Short</option>
                        <option value="short">Short</option>
                        <option value="medium">Medium</option>
                      </select>
                    </div>
                  </div>

                  <div>
                    <label className="block text-white text-sm font-medium mb-2">
                      What should this dialogue achieve?
                    </label>
                    <input
                      type="text"
                      value={beat}
                      onChange={(e) => setBeat(e.target.value)}
                      placeholder="Reveal a secret, build tension, make plans..."
                      className="w-full glass-effect rounded-lg p-3 text-white placeholder-white/40 focus:ring-2 focus:ring-cosmic-accent focus:border-transparent"
                    />
                  </div>

                </div>
              )}

              {/* Unified Generate Button */}
              <button
                onClick={handleGenerate}
                disabled={loading || !seedIdea.trim()}
                className="w-full glass-effect rounded-lg p-3 text-white hover:bg-white/10 transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center space-x-2"
              >
                {loading ? (
                  <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                ) : (
                  <>
                    <Sparkles className="w-5 h-5" />
                    <span>Generate {showDialogue ? 'Scene with Dialogue' : 'Scene'}</span>
                  </>
                )}
              </button>

              {/* Results */}
              {suggestion && (
                <div className="space-y-4">
                  <div className="glass-effect rounded-lg p-4">
                    <div className="flex items-center justify-between mb-3">
                      <h4 className="text-white font-medium">AI Suggestion</h4>
                      <div className="flex items-center space-x-2">
                        <button
                          onClick={handleCopy}
                          className="glass-effect p-2 rounded-lg text-white hover:bg-white/10 transition-colors"
                        >
                          {copied ? <Check className="w-4 h-4" /> : <Copy className="w-4 h-4" />}
                        </button>
                        <button
                          onClick={handleGenerate}
                          disabled={loading}
                          className="glass-effect p-2 rounded-lg text-white hover:bg-white/10 transition-colors"
                        >
                          <RotateCcw className="w-4 h-4" />
                        </button>
                      </div>
                    </div>
                    <p className="text-white/90 whitespace-pre-wrap">{suggestion}</p>

                    {contextUsed && (
                      <div className="mt-3 pt-3 border-t border-white/10">
                        <p className="text-white/50 text-xs">
                          {contextUsed.previousPrompts?.length > 0 && `Used context from ${contextUsed.previousPrompts.length} previous scene(s)`}
                          {contextUsed.usedLastFrame && '  Used last frame for visual continuity'}
                        </p>
                      </div>
                    )}
                  </div>

                  <button
                    onClick={handleInsert}
                    className="w-full bg-cosmic-primary hover:bg-cosmic-primary/80 text-white rounded-lg p-3 transition-colors font-medium"
                  >
                    Insert into Scene Description
                  </button>
                </div>
              )}
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default AIAssistPanel;
